import{_ as i,c as a,a2 as e,o as t}from"./chunks/framework.CBoF8BIX.js";const u=JSON.parse('{"title":"","description":"自我学习，努力上进","frontmatter":{"outline":"deep","head":[["meta",{"name":"title","content":"自我学习"}],["meta",{"name":"description","content":"自我学习，努力上进"}]]},"headers":[],"relativePath":"src-pages/novel-md/ai-story.md","filePath":"src-pages/novel-md/ai-story.md","lastUpdated":null}'),o={name:"src-pages/novel-md/ai-story.md"};function r(s,l,n,d,c,h){return t(),a("div",null,l[0]||(l[0]=[e('<h2 id="一、好故事的基本原理" tabindex="-1">一、好故事的基本原理 <a class="header-anchor" href="#一、好故事的基本原理" aria-label="Permalink to &quot;一、好故事的基本原理&quot;">​</a></h2><h3 id="_1-好故事-4-大要素" tabindex="-1">1. 好故事 4 大要素 <a class="header-anchor" href="#_1-好故事-4-大要素" aria-label="Permalink to &quot;1. 好故事 4 大要素&quot;">​</a></h3><ul><li>人物：故事要有一个【贯穿始终的人物】，整个故事围绕这个人物展开</li><li>主题：故事必须有一个【明确的主题】，必须追问出事件背后的【为什么】</li><li>冲突：故事至少有【3 大冲突形式】中的任意两个</li><li>反转：故事必须有【5 大反转】</li></ul><h3 id="_2-网络短篇故事-5-大标准" tabindex="-1">2. 网络短篇故事 5 大标准 <a class="header-anchor" href="#_2-网络短篇故事-5-大标准" aria-label="Permalink to &quot;2. 网络短篇故事 5 大标准&quot;">​</a></h3><ul><li><ol><li>【开头直给，不啰嗦】（快速进入剧情，不铺垫）</li></ol></li><li><ol start="2"><li>【采用第一人称，我来叙述我故事】（亲身经历，代入感强）</li></ol></li><li><ol start="3"><li>人物必须【极致化】（极端人物好看）</li></ol></li><li><ol start="4"><li>冲突每屏必须出现（也就是每一个手机屏幕，冲突快而多）</li></ol></li><li><ol start="5"><li>环境描写尽量少（写人为主）</li></ol></li></ul><h3 id="_3-ai-写作流程" tabindex="-1">3.AI 写作流程 <a class="header-anchor" href="#_3-ai-写作流程" aria-label="Permalink to &quot;3.AI 写作流程&quot;">​</a></h3><p>【找灵感、寻主题、磨人物、做结构、写初稿、做修改】</p><div class="tip custom-block"><p class="custom-block-title">ai 提示词</p><ol><li>故事有哪些类型（给出答案后，继续提问）</li><li>关于爱情故事，有哪些写作方向</li><li>请给我 5 个【悬疑爱情】故事的写作素材</li><li>我对【XXX】这个故事很感兴趣，请给我详细讲讲这个故事的主要内容</li><li>我需要一个中国故事，请将故事中的人物换成中国人，故事背景放在中国</li><li>很好，请帮我为这个故事寻找 5 个深刻的主题思想</li><li>我对【XXX】这个主题很满意，请将这个主题融入故事，重新撰写这个故事的梗概内容</li><li>完美，下面我们一起打磨这个故事中的人物，请写出这个故事里的主要人物的人物小传</li><li>好的，设计这个故事的其他人物角色</li><li>厉害，下面我们来为这个故事设置结构，请写出这个故事的叙事结构</li><li>这个结构不够好，换一个结构</li><li>很好，就使用这个结构，下面结合上面所有内容，我们一步一步撰写出这个故事的初稿，请先写出【第一慕：引子】的具体故事内容，要求：语言风格简洁、有悬疑风、设计好人物的冲突和故事反转，要 有符合人物性格的对话，故事悬念迭起，引人入胜，在章节结尾处，留下一个悬念 :::</li></ol><h2 id="二、故事基本构思" tabindex="-1">二、故事基本构思 <a class="header-anchor" href="#二、故事基本构思" aria-label="Permalink to &quot;二、故事基本构思&quot;">​</a></h2></div>',8)]))}const p=i(o,[["render",r]]);export{u as __pageData,p as default};
